    async def forward_responses(_, info, responses: list[dict[str, object]]) -&gt; list[bool]:
        responses = [Response(
            response_id=uuid.UUID(response["response_id"]),
            post_id=uuid.UUID(response["post_id"]),
            description=response["description"],
            apikey_id=uuid.UUID(response["apikey_id"]) if "apikey_id" in response else None, #TODO why is this not send as None?
            time=response["time"],
        ) for response in responses]

        status = []
        for response in responses:
            try:
                #await self.outer_class._resolve_event(response)
                asyncio.create_task(self.outer_class._resolve_event(response))

                status.append(True)
            except Exception as e:
                self.logger.error(f"Error resolving response.")
                status.append(False)

        return status

