            ui_id=pa_usermessage["ui_id"],
            text=pa_usermessage["text"],
        ) for pa_usermessage in pa_usermessages]

        status = []
        for usermessage in usermessages:
            try:
                #await self.outer_class._resolve_event(usermessage)
